mapper.insert:
INSERT INTO stock_strategy_deal ( `date`, `code`, `name`, `price`, `current_price`, `current_date`, `rise_ratio`, `days`, `sold`, `strategy_id` ) VALUES
( #{item.date}, #{item.code}, #{item.name}, #{item.price}, #{item.currentPrice}, #{item.currentDate}, #{item.riseRatio}, #{item.days}, #{item.sold}, #{item.strategyId} )
ON DUPLICATE KEY UPDATE `name` = VALUES(`name`), `price` = VALUES(`price`), `current_price` = VALUES(`current_price`), `current_date` = VALUES(`current_date`), `rise_ratio` = VALUES(`rise_ratio`), `days` = VALUES(`days`), `sold` = VALUES(`sold`);
