mapper.insert:
INSERT INTO stock_strategy_deal ( `date`, `code`, `name`, `price`, `sell_price`, `sell_date`, `rise_ratio`, `days`, `strategy_id` ) VALUES
( #{item.date}, #{item.code}, #{item.name}, #{item.price}, #{item.sellPrice}, #{item.sellDate}, #{item.riseRatio}, #{item.days}, #{item.strategyId} )
ON DUPLICATE KEY UPDATE `name` = VALUES(`name`), `price` = VALUES(`price`), `sell_price` = VALUES(`sell_price`), `sell_date` = VALUES(`sell_date`), `rise_ratio` = VALUES(`rise_ratio`), `days` = VALUES(`days`);
